CREATE INDEX "IX_Comments_TaskItemId" ON "Comments" ("TaskItemId");

CREATE INDEX "IX_Comments_UserId" ON "Comments" ("UserId");

CREATE INDEX "IX_Notifications_TaskItemId" ON "Notifications" ("TaskItemId");

CREATE INDEX "IX_Notifications_UserId" ON "Notifications" ("UserId");

CREATE INDEX "IX_TaskHistories_TaskItemId" ON "TaskHistories" ("TaskItemId");

CREATE INDEX "IX_TaskHistories_UserId" ON "TaskHistories" ("UserId");

CREATE INDEX "IX_TaskItems_AssignedToUserId" ON "TaskItems" ("AssignedToUserId");

CREATE INDEX "IX_TaskItems_CreatedByUserId" ON "TaskItems" ("CreatedByUserId");

CREATE INDEX "IX_TaskItems_ProjectId" ON "TaskItems" ("ProjectId");

CREATE UNIQUE INDEX "IX_Users_Email" ON "Users" ("Email");

CREATE UNIQUE INDEX "IX_Users_Username" ON "Users" ("Username");

SELECT setval(
    pg_get_serial_sequence('"Projects"', 'Id'),
    GREATEST(
        (SELECT MAX("Id") FROM "Projects") + 1,
        nextval(pg_get_serial_sequence('"Projects"', 'Id'))),
    false);

